INSERT INTO courses (title, course_order, course_description) VALUES ('Full Stack Open', 1, 'Learn React, Redux, Node.js, MongoDB, GraphQL and TypeScript in one go! This course will introduce you to modern JavaScript-based web development. The main focus is on building single page applications with ReactJS that use REST APIs built with Node.js.');
INSERT INTO courses (title, course_order, course_description) VALUES ('Designing and Building Scalable Web Applications', 2, 'The course introduces learners to the principles of building scalable web applications, focusing on recent advances in both client- and server-side development as well as on platforms and hosting solutions. Architectural patterns and their fit and need for different types of web applications are also considered.');
